import React, { useRef, useState } from 'react';
import Card from "../Card/Card";
import styles from "./Cards.module.css";

export default function Cards({ characters, onClose }) {
  const [activeCard, setActiveCard] = useState(0);
  const containerRef = useRef();

  const handleMouseMove = (event) => {
    const container = containerRef.current;
    const mouseX = event.clientX;

    // Calcula el índice de la tarjeta activa
    const cards = Array.from(container.children);
    const activeIndex = cards.findIndex((card) => {
      const cardRect = card.getBoundingClientRect();
      return mouseX >= cardRect.left && mouseX <= cardRect.right;
    });

    if (activeIndex !== -1) {
      setActiveCard(activeIndex);
    }
  };

  const handlePrev = () => {
    setActiveCard((prevCard) => (prevCard === 0 ? characters.length - 1 : prevCard - 1));
  };

  const handleNext = () => {
    setActiveCard((prevCard) => (prevCard === characters.length - 1 ? 0 : prevCard + 1));
  };

  return (
    <div className={styles.cardsWrapper}>
      <div
        ref={containerRef}
        className={styles.cardsContainer}
        onMouseMove={handleMouseMove}
      >
        <div className={styles.cardsCenter}>
          {characters.map((char, index) => (
            <Card
              key={char.id}
              id={char.id}
              name={char.name}
              status={char.status}
              species={char.species}
              gender={char.gender}
              origin={char.origin.name}
              image={char.image}
              onClose={onClose}
              className={`${styles.card} ${index === activeCard ? styles.active : ''}`}
            />
          ))}
        </div>
      </div>
      <button className={styles.arrow} onClick={handlePrev}>
        &#8249;
      </button>
      <button className={styles.arrow} onClick={handleNext}>
        &#8250;
      </button>
    </div>
  );
}
