import React, { useState, useRef, useEffect } from "react";
import Card from "../Card/Card";
import styles from "./Cards.module.css";

export default function Cards({ characters, onClose }) {
  const [mousePosition, setMousePosition] = useState(null);
  const cardContainerRef = useRef();

  useEffect(() => {
    if (mousePosition !== null && cardContainerRef.current) {
      const containerWidth = cardContainerRef.current.offsetWidth;
      const scrollWidth = cardContainerRef.current.scrollWidth;
      const maxScrollLeft = scrollWidth - containerWidth;
      const scrollLeft = (mousePosition / window.innerWidth) * maxScrollLeft;
      cardContainerRef.current.scrollLeft = scrollLeft;
    }
  }, [mousePosition]);

  return (
    <div
      ref={cardContainerRef}
      className={`${styles.mainDiv} ${styles.cardsContainer}`}
      onMouseMove={e => setMousePosition(e.clientX)}
    >
      {characters.map((char) => {
        return (
          <Card
            key={char.id}
            id={char.id}
            name={char.name}
            status={char.status}
            species={char.species}
            gender={char.gender}
            origin={char.origin.name}
            image={char.image}
            onClose={onClose}
            className={styles.card}
          />
        );
      })}
    </div>
  );
}
