import React, { useState, useEffect } from 'react';
import Card from "../Card/Card";
import styles from "./Cards.module.css";

export default function Cards({ characters, onClose }) {
  const [mousePosition, setMousePosition] = useState({ x: null, y: null });

  const handleMouseMove = (e) => {
    setMousePosition({ x: e.clientX, y: e.clientY });
  };

  useEffect(() => {
    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  useEffect(() => {
    if (mousePosition.x && mousePosition.y) {
      // aquí puedes cambiar las variables CSS en función de mousePosition
      // por ejemplo:
      document.documentElement.style.setProperty('--offset', mousePosition.x);
      document.documentElement.style.setProperty('--direction', mousePosition.y);
    }
  }, [mousePosition]);

  return (
    <div className={styles.carousel}>
      {characters.map((char, index) => (
        <div className={styles.cardContainer} key={char.id}>
          <Card
            id={char.id}
            name={char.name}
            status={char.status}
            species={char.species}
            gender={char.gender}
            origin={char.origin.name}
            image={char.image}
            onClose={onClose}
          />
        </div>
      ))}
    </div>
  );
}
