import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { addFav, removeFav } from "../../redux/actions";
import { FaTimes, FaStar } from "react-icons/fa";
import styles from "./Card.module.css";

function Card(props) {
  const {
    name,
    id,
    status,
    species,
    gender,
    origin,
    image,
    onClose,
    addFav,
    removeFav,
    myFavorites,
  } = props;

  const [isFav, setIsFav] = useState(false);

  const handleFavorite = () => {
    isFav ? removeFav(id) : addFav(props);
    setIsFav(!isFav);
  };

  useEffect(() => {
    myFavorites.forEach((fav) => {
      if (fav.id === props.id) {
        setIsFav(true);
      }
    });
  }, [myFavorites, props.id]);

  return (
    <div className={styles.card}>
      {isFav ? (
        <button className={`${styles.button} ${styles.favoriteButton}`} onClick={handleFavorite}>
          <FaStar className={styles.buttonIcon} />
        </button>
      ) : (
        <button className={`${styles.button} ${styles.favoriteButton}`} onClick={handleFavorite}>
          <FaStar className={styles.buttonIcon} />
        </button>
      )}
      <button className={`${styles.button} ${styles.deleteButton}`} onClick={() => onClose(id)}>
        <FaTimes className={styles.buttonIcon} />
      </button>
      <img src={image} alt="character" className={styles.image} />
      <div className={styles.wrapperText}>
        <Link strict to={`/detail/${id}`}>
          <h1 className={styles.name}>{name}</h1>
        </Link>
        <div className={styles.details}>
          <h2>{status}</h2>
          <h2>{species}</h2>
          <h2>{gender}</h2>
          <h2>{origin}</h2>
        </div>
      </div>
    </div>
  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    addFav: (character) => {
      dispatch(addFav(character));
    },
    removeFav: (id) => {
      dispatch(removeFav(id));
    },
  };
};

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Card);
