import React, { useState } from "react";
import "./formStyles.css"; // Ajusta la ruta al archivo CSS si es necesario
import validation from "./validation"; // Ajusta la ruta al archivo validation si es necesario

const Form = (props) => {
  const { login } = props;
  const [userData, setUserData] = useState({ email: "", password: "" });
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
    setErrors(validation({ ...userData, [e.target.name]: e.target.value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    login(userData);
  };

  return (
    <div className="container">
      <div className="box">
        <div className="form">
          <h2>Sign in</h2>
          <form onSubmit={handleSubmit}>
            <div className="inputBox">
              <input
                type="text"
                placeholder="Email..."
                name="email"
                onChange={handleChange}
                value={userData.email}
                required
              />
              <label>Email</label>
              {errors.e1 && <p className="error">{errors.e1}</p>}
              {errors.e2 && <p className="error">{errors.e2}</p>}
            </div>
            <div className="inputBox">
              <input
                type="password"
                placeholder="Password..."
                name="password"
                onChange={handleChange}
                value={userData.password}
                required
              />
              <label>Password</label>
              {errors.p1 && <p className="error">{errors.p1}</p>}
            </div>
            <button type="submit" className="loginButton">
              LOGIN
            </button>
            <div className="links">
              <a href="/">Forgot Password</a>
              <a href="/">Signup</a>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Form;
