import React, { useState, useEffect } from "react";
import { styles } from "./Detail.module.css";
import { useParams } from "react-router-dom";
import axios from "axios";

const Detail = () => {
  const { id } = useParams();
  const [character, setCharacter] = useState({});

  useEffect(() => {
    axios(`https://rickandmortyapi.com/api/character/${id}`).then(
      ({ data }) => {
        if (data.name) {
          setCharacter(data);
        } else {
          window.alert("No hay personajes con ese ID");
        }
      }
    );
    return setCharacter({});
  }, [id]);

  return (
    <div className={styles.detailContainer}>
      <h1 className={styles.name}>{character.name}</h1>
      <h1 className={styles.status}>{character.status}</h1>
      <h1 className={styles.species}>{character.species}</h1>
      <h1 className={styles.gender}>{character.gender}</h1>
      <h1 className={styles.origin}>{character.origin?.name}</h1>
      <img className={styles.image} src={character.image} alt="" />
    </div>
  );

};

export default Detail;
